import React , {useState} from "react";
import {Link} from 'react-router-dom';

const Login = () => {

  //State local
  const [usuario, setUsuario] = useState({
      email:'',
      password:''
  });

  //Extraer el usuario
  const {email,password} = usuario;

  //Al ingresar dato en los imputs
  const onChange = (e) => {
    setUsuario({
        ...usuario,
        [e.target.name] : e.target.value
    });
  };

  //Al hacer submit
  const onSubmit = (e) =>{
    e.preventDefault();

    //Validar los campos

    //Pasarlo al action

  };

  return (
    <div className="form-usuario">
      <div className="contenedor-form sombra-dark">
        <h1>Iniciar Sesión</h1>
        <form
            onSubmit={onSubmit}
        >
          <div className="campo-form">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Tu email es tu usuario"
              value={email}
              onChange={onChange}
            />
          </div>
          <div className="campo-form">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Ingresa tu password"
              value={password}
              onChange={onChange}
            />
          </div>

          <div className="campo-form">
              <input 
                type="submit"
                className="btn btn-primario btn-block"
                value="Inicias Sesión"
              />
          </div>
        </form>
        <Link to={'/nueva-cuenta'} className="enlace-cuanta">
            Obtener cuenta
        </Link>
      </div>
    </div>
  );
};

export default Login;
