import React, {Fragment, useContext} from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

import Tarea from './Tarea';
import ProyectoContext from "../../contexts/proyectos/ProyectoContext";
import TareasContext from "../../contexts/tareas/TareaContext";

const ListadoTareas = () => {
    const nodeRef = React.useRef(null); //Previene el error index.js:1 Warning: findDOMNode is deprecated in StrictMode. al usar transition-group
    //Obtener el State del inicial de los context
    //Proyectos
    const proyectosContext = useContext(ProyectoContext);
    const { proyecto, eliminarProyecto } = proyectosContext;

    //Tareas
    const tareasContext = useContext(TareasContext);
    const { tareasProyecto } = tareasContext;

    //Si no hay proyecto seleccionado
    if(!proyecto) return <h2>Selecciona un proyecto</h2>;

    //Array Desrtucturing
    const [proyectoActual] = proyecto;

    // Elimina un proyecto
    const onClickEliminar = () => {
        eliminarProyecto(proyectoActual._id)
    };

    return (
        <Fragment>
            <h2>Proyecto: {proyectoActual.nombre}</h2>
            <ul className="listado-tareas">
                {tareasProyecto.length === 0 
                    ? (<li className="tarea"><p>No hay tareas</p></li>)
                    : 
                    <TransitionGroup>
                        {tareasProyecto.map(tarea => (
                            <CSSTransition 
                                nodeRef={nodeRef}
                                key={tarea.id}
                                timeout={200}
                                classNames="tarea"
                            >
                                <Tarea
                                    tarea={tarea}
                                />
                            </CSSTransition>
                        ))}
                    </TransitionGroup>          
                }
            </ul>
            <button
                type="button"
                className="btn btn-eliminar"
                onClick={onClickEliminar}
            >Eliminar Proyecto &times;</button>
        </Fragment>
     );
}
 
export default ListadoTareas;
