import React, {useContext} from 'react';
import TareasContext from "../../contexts/tareas/TareaContext";

const Tarea = ({tarea}) => {

    //Obtener el State del inicial
    //Tareas
    const tareasContext = useContext(TareasContext);
    const { eliminarTarea } = tareasContext;

    //Eliminar tarea al presionar el boton
    const onClickEliminar = (id) =>{
        eliminarTarea(id);
    }


    return ( 
        <li className="tarea sombra">
            <p>{tarea.nombre}</p>
            <div className="estado">
               {tarea.estado
               ? 
                    (
                        <button
                            type="button"
                            className="completo"
                        >Completada</button>
                    )
               :
                    (
                        <button
                            type="button"
                            className="incompleto"
                        >No Completada</button>
                    )
               } 
            </div>
            <div clas="acciones">
                <button
                    type="button"
                    className="btn btn-primario"
                >Editar</button>

                <button
                    type="button"
                    className="btn btn-secundario"
                    onClick={() => onClickEliminar(tarea.id)}
                >Eliminar</button>
            </div>
        </li>
     );
}
 
export default Tarea;
