import React, {useReducer} from 'react';
import TareaContext from './TareaContext';
import TareaReducer from './TareaReducer';

import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA,
    ESTADO_TAREA,
    TAREA_ACTUAL
} from '../../types';

const TareaState = (props) => {
    //State inicial
    const initialState = {
        tareas: [
        {id: 1, nombre: 'Tarea 1', estado:true, proyectoId: 1},
        {id: 2, nombre: 'Tarea 1', estado:true, proyectoId: 2},
        {id: 3, nombre: 'Tarea 1', estado:true, proyectoId: 3},
        {id: 4, nombre: 'Tarea 1', estado:true, proyectoId: 4},
        {id: 5, nombre: 'Tarea 2', estado:false, proyectoId: 1},
        {id: 6, nombre: 'Tarea 2', estado:false, proyectoId: 2},
        {id: 7, nombre: 'Tarea 2', estado:false, proyectoId: 3},
        {id: 8, nombre: 'Tarea 2', estado:false, proyectoId: 4},
        {id: 9, nombre: 'Tarea 3', estado:true, proyectoId: 1},
        {id: 10, nombre: 'Tarea 3', estado:false, proyectoId: 2},
        {id: 11, nombre: 'Tarea 3', estado:true, proyectoId: 3},
        {id: 12, nombre: 'Tarea 3', estado:false, proyectoId: 4},
        {id: 13, nombre: 'Tarea 4', estado:false, proyectoId: 1},
        {id: 14, nombre: 'Tarea 4', estado:false, proyectoId: 2},
        {id: 15, nombre: 'Tarea 4', estado:false, proyectoId: 4}
     ],
     tareasProyecto: null,
     errorTarea: false,
     tareaSeleccionada: null
    };

    //Crear Dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);

    //Obtener las tareas de un proyecto
    const obtenerTareas = (proyectoId) => {
        dispatch({
            type: TAREAS_PROYECTO,
            payload: proyectoId
        })
    };

    //Agregar una tarea al proyecto seleccionado
    const agregarTarea = (tarea) => {
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea
        })
    };
    
    //Validacion
    const validarTarea = () => {
        dispatch({ 
            type: VALIDAR_TAREA
        })
    };

    //ELiminar tarea
    const eliminarTarea = (tareaId) => {
        dispatch({
            type: ELIMINAR_TAREA,
            payload: tareaId
        })
    };

    //Cambie estado de la tarea
    const cambiaEstadoTarea = (tarea) =>{
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea
        })
    };

    //Extrae tarea para edicion
    const guardarTareaActual = (tarea) =>{
        dispatch({
            type: TAREA_ACTUAL,
            payload: tarea
        }) 
    };

    return(
        <TareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasProyecto: state.tareasProyecto,
                errorTarea: state.errorTarea,
                tareaSeleccionada: state.tareaSeleccionada,
                obtenerTareas,
                agregarTarea,
                validarTarea,
                eliminarTarea,
                cambiaEstadoTarea,
                guardarTareaActual
            }}
        >
            {props.children}
        </TareaContext.Provider>
    );
}

export default TareaState;
