import React from 'react';
import TareasContext from "../../contexts/tareas/TareaContext";
// eliminarTarea

const Tarea = ({tarea}) => {
    return ( 
        <li className="tarea sombra">
            <p>{tarea.nombre}</p>
            <div className="estado">
               {tarea.estado
               ? 
                    (
                        <button
                            type="button"
                            className="completo"
                        >Completada</button>
                    )
               :
                    (
                        <button
                            type="button"
                            className="incompleto"
                        >No Completada</button>
                    )
               } 
            </div>
            <div clas="acciones">
                <button
                    type="button"
                    className="btn btn-primario"
                >Editar</button>

                <button
                    type="button"
                    className="btn btn-secundario"
                >Eliminar</button>
            </div>
        </li>
     );
}
 
export default Tarea;
