import React, {useReducer} from 'react';
import TareaContext from './TareaContext';
import TareaReducer from './TareaReducer';

import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA
} from '../../types';

const TareaState = (props) => {
    //State inicial
    const initialState = {
        tareas: [
        {nombre: 'Tarea 1', estado:true, proyectoId: 1},
        {nombre: 'Tarea 1', estado:true, proyectoId: 2},
        {nombre: 'Tarea 1', estado:true, proyectoId: 3},
        {nombre: 'Tarea 1', estado:true, proyectoId: 4},
        {nombre: 'Tarea 2', estado:false, proyectoId: 1},
        {nombre: 'Tarea 2', estado:false, proyectoId: 2},
        {nombre: 'Tarea 2', estado:false, proyectoId: 3},
        {nombre: 'Tarea 2', estado:false, proyectoId: 4},
        {nombre: 'Tarea 3', estado:true, proyectoId: 1},
        {nombre: 'Tarea 3', estado:false, proyectoId: 2},
        {nombre: 'Tarea 3', estado:true, proyectoId: 3},
        {nombre: 'Tarea 3', estado:false, proyectoId: 4},
        {nombre: 'Tarea 4', estado:false, proyectoId: 1},
        {nombre: 'Tarea 4', estado:false, proyectoId: 2},
        {nombre: 'Tarea 4', estado:false, proyectoId: 4}
     ],
     tareasProyecto: null
    };

    //Crear Dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);

    //Obtener las tareas de un proyecto
    const obtnerTareas = (proyectoId) => {
        dispatch({
            type: TAREAS_PROYECTO,
            payload: proyectoId
        })
    };

    //Agregar una tarea al proyecto seleccionado
    const agregarTarea = (tarea) => {
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea
        })
    };

    return(
        <TareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasProyecto: state.tareasProyecto,
                obtnerTareas,
                agregarTarea
            }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState;
