import React, {useContext} from 'react';
import proyectoContext from "../../contexts/proyectos/ProyectoContext";
import tareasContext from "../../contexts/tareas/TareaContext";

const Tarea = ({tarea}) => {

    //Obtener el State del inicial
    //Proyectos
    const proyectosContext = useContext(proyectoContext);
    const { proyecto } = proyectosContext;
    //Tareas
    const tareasContext = useContext(tareasContext);
    const { obtenerTareas, eliminarTarea, modificaTarea, guardarTareaActual } = tareasContext;

    //Extraigo el proyecto actual
    const [proyectoActual] = proyecto;

    //Eliminar tarea al presionar el boton
    const onClickEliminar = (id) =>{
        eliminarTarea(id,proyectoActual._id);
        obtenerTareas(proyectoActual.id)
    }

    //Modifica el esta de las tareas
    const cambiaEstado = (tarea) => {
        if(tarea.estado){
            tarea.estado = false;
        }else{
            tarea.estado = true; 
        };
        modificaTarea(tarea);
    };

    //Editar la tarea
    const seleccionatTarea = (tarea) =>{
        guardarTareaActual(tarea);
    };

    return ( 
        <li className="tarea sombra">
            <p>{tarea.nombre}</p>
            <div className="estado">
               {tarea.estado
               ? 
                    (
                        <button
                            type="button"
                            className="completo"
                            onClick={() => cambiaEstado(tarea)}
                        >Completada</button>
                    )
               :
                    (
                        <button
                            type="button"
                            className="incompleto"
                            onClick={() => cambiaEstado(tarea)}
                        >No Completada</button>
                    )
               } 
            </div>
            <div clas="acciones">
                <button
                    type="button"
                    className="btn btn-primario"
                    onClick={() => seleccionatTarea(tarea)}
                >Editar</button>

                <button
                    type="button"
                    className="btn btn-secundario"
                    onClick={() => onClickEliminar(tarea._id)}
                >Eliminar</button>
            </div>
        </li>
     );
}
 
export default Tarea;
