import {
  TAREAS_PROYECTO,
  AGREGAR_TAREA,
  VALIDAR_TAREA,
  ELIMINAR_TAREA,
  ESTADO_TAREA,
  TAREA_ACTUAL,
  MODIFICAR_TAREA,
  LIMPIAR_TAREA
} from "../../types";

// eslint-disable-next-line
export default (state, action) => {
  switch (action.type) {
    case TAREAS_PROYECTO:
      return {
        ...state,
        tareasProyecto: state.tareas.filter(
          (tarea) => tarea.proyecto === action.payload
        )
      };
    case AGREGAR_TAREA:
      return {
        ...state,
        tareasProyecto: [action.payload, ...state.tareas],
        errorTarea: false,
      };
    case VALIDAR_TAREA:
      return {
        ...state,
        errorTarea: true,
      };
    case ELIMINAR_TAREA:
      return {
        ...state,
        tareasProyecto: state.tareasProyecto.filter((tarea) => tarea.id !== action.payload)
      };
    case MODIFICAR_TAREA: //Escucha cualquiera de los dos y ejecuta (Es el mismo codigo)
    case ESTADO_TAREA:
      return {
        ...state,
        tareasProyecto: state.tareasProyecto.map((tarea) =>
          tarea.id === action.payload.id ? action.payload : tarea
        )
      };
    case TAREA_ACTUAL:
      return {
        ...state,
        tareaSeleccionada: action.payload
      };
    case LIMPIAR_TAREA:
       return {
        ...state,
        tareaSeleccionada: null
       };
    default:
      return state;
  }
};
