import React, {useContext, useEffect} from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

import Proyecto from './Proyecto'
import ProyectoContext from "../../contexts/proyectos/ProyectoContext";
import AlertaContext from "../../contexts/alertas/AlertaContext";

const ListadoProyectos = () => {
    const nodeRef = React.useRef(null); //Previene el error index.js:1 Warning: findDOMNode is deprecated in StrictMode.
    //Obtener el State del inicial
    //Proyecto
    const proyectosContext = useContext(ProyectoContext);
    const { mensaje, proyectos, obtenerProyectos } = proyectosContext;
    //Alerta
    const alertaContext = useContext(AlertaContext);
    const { alerta, mostrarAlerta } = alertaContext;

    useEffect(() => {
        //Si hay un error
        if(mensaje){
            mostrarAlerta(mensaje.msg, mensaje.categoria);
        }
        obtenerProyectos();
    // eslint-disable-next-line 
    }, []);

    if(proyectos.length === 0) return <p>No hay proyectos, agrega uno</p>;

    return ( 
        <ul className="listado-proyectos">
            <TransitionGroup>
                {proyectos.map(proyecto => (
                    <CSSTransition
                        nodeRef={nodeRef}
                        key={proyecto._id}
                        timeout={200}
                        classNames="proyecto"
                    >
                        <Proyecto
                            proyecto={proyecto}
                        />
                    </CSSTransition>
                ))}
            </TransitionGroup>
        </ul>
     );
}
 
export default ListadoProyectos;
