import React, { Fragment } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import Login from "./components/auth/Login";
import NuevaCuenta from "./components/auth/NuevaCuenta";
import Proyectos from "./components/proyectos/Proyectos";

//Contexts
import ProyectoState from "./contexts/proyectos/ProyectoState";
import TareaState from "./contexts/tareas/TareaState";
import AlertaState from "./contexts/alertas/AlertaState";
import AuthState from "./contexts/autenticacion/AuthState";
import RutaPrivada from "./components/rutas/RutaPrivada";

//Config
import tokenAuth from "./config/tokenAuth";

//Revisamos si hay un token
const token = localStorage.getItem('token');
if(token){
  tokenAuth(token);
}

function App() {
  return (
    <Fragment>
      <ProyectoState>
        <TareaState>
          <AlertaState>
            <AuthState>
              <Router>
                <Switch>
                  <Route exact path="/" component={Login} />
                  <Route exact path="/nueva-cuenta" component={NuevaCuenta} />
                  {/* <RutaPrivada exact path="/proyectos" component={Proyectos} /> */}
                  <Route exact path="/proyectos" component={Proyectos} />
                </Switch>
              </Router>
            </AuthState>
          </AlertaState>
        </TareaState>
      </ProyectoState>
    </Fragment>
  );
}

export default App;
