import React, { useContext, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import AlertaContext from "../../contexts/alertas/AlertaContext";
import AuthContext from "../../contexts/autenticacion/AuthContext";

const NuevaCuenta = (props) => {
  //Extraer el constext
  //Alerta
  const alertaContext = useContext(AlertaContext);
  const { alerta, mostrarAlerta } = alertaContext;
  //Auth
  const authContext = useContext(AuthContext);
  const { autenticado, mensaje, registrarUsuario } = authContext;

  //Se ejecuta despues del submit
  useEffect(() => {
    if(autenticado){
      props.history.push('/proyectos');
    }

    if(mensaje){
      mostrarAlerta(mensaje.msg,mensaje.categoria);
    }


  }, [mensaje, autenticado, props.history]); //Escucha a mensaje y autenticado

  //State local
  const [usuario, setUsuario] = useState({
    nombre: "",
    email: "",
    password: "",
    confirmar: "",
  });

  //Extraer el usuario
  const { nombre, email, password, confirmar } = usuario;

  //Al ingresar dato en los imputs
  const onChange = (e) => {
    setUsuario({
      ...usuario,
      [e.target.name]: e.target.value,
    });
  };

  //Al hacer submit
  const onSubmit = (e) => {
    e.preventDefault();

    //Validar los campos
    if (
      nombre.trim() === "" ||
      email.trim() === "" ||
      password.trim() === "" ||
      confirmar.trim() === ""
    ) {
      mostrarAlerta("Todos los campos son obligatorios", "alerta-error");
      return;
    }

    //Password minimo 6 caracteres
    if(password.length < 6){
      mostrarAlerta("El password debe tener al menos 6 caracteres", "alerta-error");
      return;
    }

    //Los 2 passwords iguales
    if(password !== confirmar){
      mostrarAlerta("Ambos passwords deben ser iguales", "alerta-error");
      return;
    }

    //Registrar
    registrarUsuario({
      nombre,
      email,
      password
    })
    
  };

  return (
    <div className="form-usuario">
      {alerta ? (
            <div className={`alerta ${alerta.categoria}`}>{alerta.msg}</div>
          ) : null}
      <div className="contenedor-form sombra-dark">
        <h1>Obtener una cuenta</h1>
        <form onSubmit={onSubmit}>
          <div className="campo-form">
            <label htmlFor="nombre">Nombre</label>
            <input
              type="text"
              id="nombre"
              name="nombre"
              placeholder="Tu Nombre"
              value={nombre}
              onChange={onChange}
            />
          </div>
          <div className="campo-form">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Ingresa tu email"
              value={email}
              onChange={onChange}
            />
          </div>
          <div className="campo-form">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Ingresa tu password"
              value={password}
              onChange={onChange}
            />
          </div>
          <div className="campo-form">
            <label htmlFor="confirmar">Confirmar Password</label>
            <input
              type="password"
              id="confirmar"
              name="confirmar"
              placeholder="Repite tu password"
              value={confirmar}
              onChange={onChange}
            />
          </div>
          <div className="campo-form">
            <input
              type="submit"
              className="btn btn-primario btn-block"
              value="Registrar"
            />
          </div>
        </form>
        <Link to={"/"} className="enlace-cuenta">
          Volver a Iniciar sesión
        </Link>
      </div>
    </div>
  );
};

export default NuevaCuenta;
