import {
        FORMULARIO_PROYECTO,
        OBTENER_PROYECTOS,
        AGREAR_PROYECTO
} from '../../types'

export default (state, action) => {
    switch(action.type){
        case FORMULARIO_PROYECTO:
            return{
                ...state,
                formulario: true
            }
        case OBTENER_PROYECTOS:
            return{
                ...state,
                proyectos: action.payload
            }
        case AGREAR_PROYECTO:
            return{
                ...state,
                proyectos: [...state.proyectos, action.payload]
            }
        default:
            return state;
    }
}
