import React, {useReducer} from 'react';
import { v4 as uuidv4 } from 'uuid';

import TareaContext from './TareaContext';
import TareaReducer from './TareaReducer';

import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA,
    ESTADO_TAREA,
    TAREA_ACTUAL,
    MODIFICAR_TAREA,
    LIMPIAR_TAREA
} from '../../types';

const TareaState = (props) => {
    //State inicial
    const initialState = {
     tareasProyecto: null,
     errorTarea: false,
     tareaSeleccionada: null
    };

    //Crear Dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);

    //Obtener las tareas de un proyecto
    const obtenerTareas = (proyectoId) => {
        dispatch({
            type: TAREAS_PROYECTO,
            payload: proyectoId
        })
    };

    //Agregar una tarea al proyecto seleccionado
    const agregarTarea = (tarea) => {
        tarea.id = uuidv4();
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea
        })
    };

    //Modificar la tarea seleccionada
    const modificaTarea = (tarea) => {
        dispatch({
            type: MODIFICAR_TAREA,
            payload: tarea
        })
    };
    
    //Validacion
    const validarTarea = () => {
        dispatch({ 
            type: VALIDAR_TAREA
        })
    };

    //ELiminar tarea
    const eliminarTarea = (tareaId) => {
        dispatch({
            type: ELIMINAR_TAREA,
            payload: tareaId
        })
    };

    //Cambie estado de la tarea
    const cambiaEstadoTarea = (tarea) =>{
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea
        })
    };

    //Extrae tarea para edicion
    const guardarTareaActual = (tarea) =>{
        dispatch({
            type: TAREA_ACTUAL,
            payload: tarea
        }) 
    };

    //Limpia la tarea seleccionada
    const limpiarTarea = () => {
        dispatch({
            type: LIMPIAR_TAREA
        }) 
    };

    return(
        <TareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasProyecto: state.tareasProyecto,
                errorTarea: state.errorTarea,
                tareaSeleccionada: state.tareaSeleccionada,
                obtenerTareas,
                agregarTarea,
                validarTarea,
                eliminarTarea,
                cambiaEstadoTarea,
                guardarTareaActual,
                modificaTarea,
                limpiarTarea
            }}
        >
            {props.children}
        </TareaContext.Provider>
    );
}

export default TareaState;
