import {
  REGISTRO_EXITOSO,
  REGISTRO_ERROR,
  OBETENER_USUARIO,
  LOGIN_EXITOSO,
  LOGIN_ERRONEO,
  CERRAR_SESION,
} from "../../types";

// eslint-disable-next-line
export default (state, action) => {
  switch (action.type) {
    case REGISTRO_EXITOSO:
      localStorage.setItem('token',action.payload.token);
      return {
        ...state,
        autenticado: true,
        mensaje:null
      };
    case REGISTRO_ERROR:
      return {
        ...state,
        token: null,
        mensaje: action.payload
      };
