import React , {useState, useContext, useEffect } from "react";
import {Link} from 'react-router-dom';
import AlertaContext from "../../contexts/alertas/AlertaContext";
import AuthContext from "../../contexts/autenticacion/AuthContext";

const Login = () => {
    //Extraer el constext
  //Alerta
  const alertaContext = useContext(AlertaContext);
  const { alerta, mostrarAlerta } = alertaContext;
  //Auth
  const authContext = useContext(AuthContext);
  const { autenticado, mensaje, iniciarSesion } = authContext;

  //State local
  const [usuario, setUsuario] = useState({
      email:'',
      password:''
  });

  //Se ejecuta despues del submit
  useEffect(() => {
      // if(autenticado){
      //   props.history.push('/proyectos');
      //   return;
      // }
  
    if(mensaje){
      mostrarAlerta(mensaje.msg, mensaje.categoria);
      return;
    }
  });

  //Extraer el usuario
  const {email,password} = usuario;

  //Al ingresar dato en los imputs
  const onChange = (e) => {
    setUsuario({
        ...usuario,
        [e.target.name] : e.target.value
    });
  };

  //Al hacer submit
  const onSubmit = (e) =>{
    e.preventDefault();

    //Validar los campos
    if(email.trim() === '' || password.trim() === ''){
      mostrarAlerta("Todos los campos son obligatorios", "alerta-error");
      return;
    }

    //Pasarlo al action
    iniciarSesion({email, password});

  };

  return (
    <div className="form-usuario">
      <div className="contenedor-form sombra-dark">
      {alerta ? (
            <div className={`alerta ${alerta.categoria}`}>{alerta.msg}</div>
          ) : null}
        <h1>Iniciar Sesión</h1>
        <form
            onSubmit={onSubmit}
        >
          <div className="campo-form">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Tu email es tu usuario"
              value={email}
              onChange={onChange}
            />
          </div>
          <div className="campo-form">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Ingresa tu password"
              value={password}
              onChange={onChange}
            />
          </div>
          <div className="campo-form">
              <input 
                type="submit"
                className="btn btn-primario btn-block"
                value="Inicias Sesión"
              />
          </div>
        </form>
        <Link to={'/nueva-cuenta'} className="enlace-cuenta">
            Obtener cuenta
        </Link>
      </div>
    </div>
  );
};

export default Login;
