import React, {useContext, useState, useEffect} from 'react';

import ProyectoContext from "../../contexts/proyectos/ProyectoContext";
import TareasContext from "../../contexts/tareas/TareaContext";

const FormTarea = () => {
    //Obtener el State del inicial
    //Proyectos
    const proyectosContext = useContext(ProyectoContext);
    const { proyecto } = proyectosContext;

    //Tareas
    const tareasContext = useContext(TareasContext);
    const { errorTarea, agregarTarea, validarTarea, obtenerTareas, tareaSeleccionada, modificaTarea } = tareasContext;

    //State inicial del formulario
    const [tarea, guardarTarea] = useState({
        nombre: ''
    });

    const {nombre} = tarea;

    //Effect para cargar tarea seleccionada
    useEffect(()=>{
        if(tareaSeleccionada !== null){
            guardarTarea(tareaSeleccionada);
        } else {
            guardarTarea({
                nombre:''
            });
        };
    },[tareaSeleccionada])

    //Si no hay proyecto seleccionado
    if(!proyecto) return null;

    //Array Desrtucturing
    const [proyectoActual] = proyecto;

    //Leer el form
    const handleTarea = (e) =>{
        guardarTarea({
            ...tarea,
            [e.target.name]:e.target.value
        })
    };

    //Hacer submit
    const onSubmit = (e) => {
        e.preventDefault();
        
        //Validar
        if(nombre.trim() === ''){
            validarTarea();
            return;
        }

        //Condicional para ver si estamos agregando o editando
        if(tareaSeleccionada === null){ //Tarea nueva
            //Agregar nueva tarea al state
            tarea.proyectoId = proyectoActual.id;
            tarea.estado = false;
            agregarTarea(tarea);
        } else {
            
        }

        //Obtener tareas
        obtenerTareas(proyectoActual.id);

        //Reiniciar el form
        guardarTarea({
            nombre:''
        })

    };

    return ( 
        <div className="formulario">
            <form
                onSubmit={onSubmit}
            >
                <div className="contenedor-input">
                    <input 
                        type="text"
                        className="input-text"
                        placeholder="Nombre Tarea"
                        name="nombre"
                        value={nombre}
                        onChange={handleTarea}
                    />
                </div>
                <div className="contenedor-input">
                <input 
                        type="submit"
                        className="btn btn-primario btn-submit btn-block"
                        placeholder="Nombre Tarea"
                        value={tareaSeleccionada ? "Editar Tarea" : "Agregar Tarea"}
                    />
                </div>
            </form>
            {errorTarea ? <p className="mensaje error">El campo es obligatorio</p> : null}
        </div>
     );
}
 
export default FormTarea;
