import {
  REGISTRO_EXITOSO,
  REGISTRO_ERROR,
  OBTENER_USUARIO,
  LOGIN_EXITOSO,
  LOGIN_ERRONEO,
  CERRAR_SESION,
} from "../../types";

// eslint-disable-next-line
export default (state, action) => {
  switch (action.type) {
    case REGISTRO_EXITOSO:
      localStorage.setItem('token',action.payload.token);
      return {
        ...state,
        autenticado: true,
        mensaje:null
      };
    case LOGIN_ERRONEO:
    case REGISTRO_ERROR:
    case CERRAR_SESION:
      localStorage.removeItem('token');
      return {
        ...state,
        token: null,
        mensaje: action.payload
      };
    case OBTENER_USUARIO:
      return {
        ...state,
        autenticado: true,
        usuario: action.payload 
        // cargando: false
      };
    // case LOGIN_EXITOSO:
    //   return {

    //   };
    default:
      return state;
  }
};
